bool melodyPlayerLoopMelody = false;   
String melodyPlayerMelodyName;
unsigned long melodyPlayerPlayStarted = 0;

void melodyPlayerSetMelodyName(String _name){
  melodyPlayerMelodyName=_name;
}
//return true if was played completely or false if interrupted
bool melodyPlayerPlayMelody(const int* melody, bool alarm) {
  Serial.println(F("Open: PlayMelody"));
  clearScreenAnimation();
  backlightOn();
  melodyPlayerPlayStarted = millis();
  //modeLoop = melodyPlayerDrawScreen;
  modeButtonUp = 0;
  modeButtonCenter = 0;
  modeButtonDown = 0;
  modeButtonUpLong = 0;
  modeButtonCenterLong = 0;
  modeButtonDownLong = 0;
  enableAutoReturn = false;
  melodyPlayerLoopMelody = false;
  
  int length = melodyPlayerGetLength(melody);
  Serial.print("length="); Serial.println(length);
  int prequencyThreshold = 0;
  {//analyze melody
    long noteSum = 0;
    long noteCnt = 0;
    for (int i = 1; i < length-1; i++) {
      int n = (melody[i]);
      if (n > 0){
        noteSum += n;
        noteCnt ++;
      }
    }
    prequencyThreshold = 100 + noteSum/noteCnt;
    Serial.print("frequencyThreshold="); Serial.println(prequencyThreshold);
  }
  do{
    melodyPlayerDrawScreen(alarm);
    for (int i = 0; i < length-1; i++) {
      int note = (melody[i]);
      if(note > 0){ //it's frequency
        buzTone(note);
        if(note>prequencyThreshold)
          ledFlashlightOnBottom();
        else
          ledFlashlightOnTop();
      }
      if(note < 0){ //it's time
        for(unsigned long noteStarted = millis(); millis() - noteStarted < (-note); ) {
          if((millis() - noteStarted)+150 < (-note)){
            if(melodyPlayerProcessButtons(alarm)) 
              return false; //false = melody interrupted
            melodyPlayerDrawScreen(alarm);
          }
        }
        if(melodyPlayerProcessButtons(alarm)) 
          return false; //false = melody interrupted
        ledFlashlightOffAll();
        buzNoTone();
      }  
    }
    delay(500);
  }while(melodyPlayerLoopMelody);
  if(!alarm) modeSetup();
  return true;
}

//returns true if user stopped playing
bool melodyPlayerProcessButtons(bool alarm){
  if(isButtonCenterPressed())
  {
    buttonBeep(); 
    modeSetup(); 
    ledFlashlightOffAll(); 
    while(isButtonCenterPressed()); 
    return true;
  }
  if(alarm){
    if(!isTimerRunning())
    {  
      if(isButtonUpPressed())
      {
        buttonBeep(); 
        modeSetup(); 
        setTimerToMinutes(10); 
        drawMessage("Відкладено на 10 хвилин");  
        ledFlashlightOffAll(); 
        buzNoTone();
        while(isButtonUpPressed()); 
        return true;
      }
      if(isButtonDownPressed())
      {
        buttonBeep(); 
        modeSetup(); 
        setTimerToMinutes(5); 
        drawMessage("Відкладено на 5 хвилин");  
        ledFlashlightOffAll(); 
        buzNoTone();
        while(isButtonDownPressed()); 
        return true;
      }
    }
  }
  else{
    if(isButtonDownPressed())
    {
      buttonBeep(); 
      melodyPlayerLoopMelody = !melodyPlayerLoopMelody; 
      drawMessage(melodyPlayerLoopMelody?"Повтор увімкнено":"Вимкнено повтор");  
      while(isButtonDownPressed()); 
    }
  }
  return false;
}

void melodyPlayerDrawScreen(bool alarm) {

  lcd()->setColorIndex(white);
  lcd()->drawBox(0, 0, 400, 240);

  lcd()->setFont(u8g2_font_10x20_t_cyrillic);  //ok
  lcd()->setCursor(5, 18); 
  lcd()->setColorIndex(black);
  if(alarm)
    lcd()->print("Сповіщення");
  else
    lcd()->print("Плеєр");
  
  int x = drawStatusbar(363, 1, true); 
  if(melodyPlayerLoopMelody)      //draw repeat icon next to statusbar
    draw_ic16_repeat(x-16, 5, black);
  

  displayDrawVector(getPathZubat(), 130, 45, 3.0, 3, false, black);
  
  lcd()->setFont(u8g2_font_10x20_t_cyrillic);  //ok
  lcd()->setCursor(5, 230); 
  int sec = (millis()-melodyPlayerPlayStarted)/1000;
  if(sec < 10)
    lcd()->print("0");
  lcd()->print(sec);
  lcd()->print("s");

  drawCentered(melodyPlayerMelodyName, 200);

  lcd()->drawBox(369, 0, 2, 260);  //draw_ic16_repeat  draw_ic16_arrow_right  draw_ic16_back
  if(alarm){
    draw_ic16_back(lx(), ly2(), black);
    if(!isTimerRunning()){  
      lcd()->setFont(u8g2_font_unifont_t_cyrillic); //smalll 
      lcd()->setCursor(lx()-5, ly1()+16); lcd()->print("+10");
      lcd()->setCursor(lx()-1, ly3()+16); lcd()->print("+5");
    }
  }
  else{
    draw_ic16_back(lx(), ly2(), black);
    draw_ic16_repeat(lx(), ly3(), black);
  }
  lcd()->sendBuffer();
}

void printBits(byte myByte) {
  for (byte mask = 0x80; mask; mask >>= 1) {
    if (mask & myByte)
      Serial.print('1');
    else
      Serial.print('0');
  }
}

int melodyPlayerGetLength(const int* melody) {
  for (int i = 0; i < 5000; i++) {
    //Serial.print("i="); Serial.println(i);
    int b = (melody[i]);
    if (b == 19) { //EOF
      return i + 1;
    }
  }
  return 5000;
}

















